import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';

import UserList from './UserList';
import UserHeader from './UserHeader';
//import UserForm from './UserForm';

import axios from 'axios';
import Settingicon from './SettingsIcon'
import UserSelector from './UserSelector'
import 'semantic-ui-css/semantic.min.css'

import { Form, Table, Message } from 'semantic-ui-react'


class App extends Component{
  state = {
    persons: []
  }

  componentDidMount() {
    axios.get(`http://localhost:8080/users`)
      .then(res => {
        const persons = res.data;
        console.log("Zapi:" + JSON.stringify(persons))
        this.setState({ persons });
      })
  }

  render(){
    console.log("New Table")
    return (
      <div id ='root' >
      <div id ='maindiv' className='mainCo'>
       <Settingicon/>
        <Table celled id='persons' className='mainCo'>
          <UserHeader users={this.state.persons}/>
          <UserList users={this.state.persons}/>
        </Table>

      </div>
        <div id='transaction'>
        <Message>
        <Message.Header>Make a Transaction</Message.Header>
        <p>
          Select 2 users to make a wallet transaction
        </p>
        </Message>
        <UserSelector users={this.state.persons} />
        <UserSelector users={this.state.persons} />
        </div>
      </div>
    )
    // <UserSelector users={this.state.persons.map(u => {return(u.id)})} />


   }

}

export default App;
