// const UserSelector = (users) => (
//   <Select placeholder='Select your country' options={options} />
// )


class UserSelector extends Component {

    changeWallet(event) {
        //JSON.stringify(event);
        console.log("Zapi event:" + JSON.stringify(event));

    }
    render(){
        return (
        <div>
          <Select placeholder='Choose and User ID' 
          onChange={this.changeWallet.bind(this)}
          options={this.props.users.map(u => {return ({key:u.id, value : u.id, text:u.id + " " + u.name})})}/>

        <Select placeholder='Select a Wallet' 
          options={this.props.users.map(u => {return (
              {key  : u.walletList?.[0]?.hash, 
              value : u.walletList?.[0]?.hash, 
              text  : "Wallet: " + u.walletList?.[0]?.hash})})}/>

        </div>
        )
    }

}
export default UserSelector;
